import { createMuiTheme } from '@material-ui/core/styles';
import { red, amber, lime } from '@material-ui/core/colors';

// Create a theme instance.
const theme = createMuiTheme({
  palette: {
    primary: amber,
    secondary: lime,
    error: {
      main: red.A400,
    },
    background: {
      default: '#fff',
    },
  },
});

export default theme;
