import AppBar from "@material-ui/core/AppBar"
import Button from "@material-ui/core/Button"
import Toolbar from "@material-ui/core/Button"
import IconButton from '@material-ui/core/IconButton';
import AccountCircle from '@material-ui/icons/AccountCircle';

import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import Link from "next/link"

export default function Navbar({ user }) {
	const [anchorEl, setAnchorEl] = React.useState(null);
	const open = Boolean(anchorEl);

	const handleMenu = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	return (
		<AppBar position="static">
			<Toolbar>
				<Link href="/"><Button variant="contained" color="secondary">Recipes</Button></Link>
				<Link href="/create-recipe"><Button variant="contained" color="primary">Make new recipe</Button></Link>
				<IconButton aria-label="account of current user" aria-controls="menu-appbar" aria-haspopup="true" onClick={handleMenu} color="inherit" >
					<AccountCircle />
				</IconButton>
				<Menu
					id="menu-appbar"
					anchorEl={anchorEl}
					anchorOrigin={{
						vertical: 'top',
						horizontal: 'right',
					}}
					keepMounted
					transformOrigin={{
						vertical: 'top',
						horizontal: 'right',
					}}
					open={open}
					onClose={handleClose}
				>

					{user && (
            <div>
							<Link href="/profile">
								<MenuItem onClick={handleClose}>Profile</MenuItem>
							</Link>
							<Link href="/logout">
								<MenuItem onClick={handleClose}>Log Out</MenuItem>
							</Link>
            </div>
					)}

					{!user && (
						<Link href="/login">
							<MenuItem onClick={handleClose}>Log In</MenuItem>
						</Link>
					)}
				</Menu>
			</Toolbar>
		</AppBar>
	)}
